import { Link } from 'react-router-dom';
import { data } from '../../mock/dummyData';
import './UsersList.scss';

 function UsersList() {
    const [users, setUsers] = useState(data);


  return (
    <div>
        {users.map(user => (
            <Link to={'user/' + user.id} >
                <div className='user-container'>
                    <p>{user.name}</p>

                        <svg fill="red" 
                            xmlns="http://www.w3.org/2000/svg" 
                            viewBox="0 0 30 30" 
