import React, { useState, useEffect } from 'react';
import {useParams} from 'react-router-dom';
import { data } from '../../mock/dummyData';

 function SingleUserPage(props) {
    const [userId, setUserId] = useState(null);
    const [user, setUser] = useState({});

    const { id } = useParams();

    useEffect(() => {
        setUserId(id);

        setUser(data.find(user => user?.id == id));
    }, [user])

  return (
    <div>
        <div><img src={user?.image}/></div> 
        <h1 className='user-value'>{user?.title}</h1> <br />
        <span className='user-value'>{user?.body}</span> <br />   


    </div>
  )
}

export default SingleUserPage;
