import React, {useEffect, useState} from 'react';
import { Link } from 'react-router-dom';
import { data } from '../../mock/dummyData';
import { news} from '../../mock/LajmeData';
import './Sport.scss'


 function SportPage() {
    const [users, setUsers] = useState(data);
    const [newses, setNews] = useState(news);

  return (
    <div className='he'>
      
    
          <div className='all-foto'>
    <div>
        <div className='header'>
         
           
            <Link to={'ekon/'}> <img src="" alt="Logo" /><p>Notat e lojtarëve, Brighton 4-0 Manchester United: Trossard shkëlqen, De Gea dështim </p></Link>
        </div>
        <div className='container'>
        {newses.map(n =>(
     
            <Link to={'news/' + n.id} >
             
                  <div className='container-user'>
                  <img className='container-image' src={n.image}/>
                  <p className='container-paragraf'>{n.title}</p>        
                  <div className='br'></div>
        </div>
            </Link>
        ))}
        </div>
    </div>

    </div>
    <div>
      <h1 className='header-text'>Botë</h1>
    {users.map(user => (
            <Link to={'user/' + user.id} >
             
                  <div className='user-container'>
                    <img className='image' src={user.image}/>
                    <p className='paragraf'>{user.title}</p>        
                    <div className='br'></div>
        </div>
            </Link>
        ))}
    </div>
    </div>
  )
}

export default UsersList;
